$(document).ready(function() {

	$('#appNotes-success').hide();
	$('#appNotes-error').hide();
	
	//check the message ID that is coming from the CMS vs. the one that's saved
	//or not
	try {
		//this will be the node ID that was saved with the notes
		var lastSavedMessageDate = localStorage.getItem('hcc-savedMessageDate');
		//this is the node ID of the current message from the CMS
		var currentMessageDate = $('input[name="messageDate"]').val();		

		if (lastSavedMessageDate == currentMessageDate) {
			var notesText = localStorage.getItem('hcc-savedMessageNotes');
		
			if (notesText != '') {
				$('#notes').val(notesText);		
			} else {
				$('#notes').val('Write your notes here');		
			}
		} else {
			//if the two aren't equal, that means there's a newer message
			//so reset the notes
			$('#notes').val('Write your notes here');
			//update the message ID to the new one
			localStorage.setItem('hcc-savedMessageNotes','');
			localStorage.setItem('hcc-savedMessageDate', currentMessageDate);
		} 

		$('#notes').focus(function(e) {
			if ( $(this).val() == 'Write your notes here' ) {
				$(this).val('');
			}

			//start note saving/checking
			autoSaveNotes();

		});		

	} catch (e) {
		//NO local storage available
		//start with a blank set of notes
		$('#notes').val('Write your notes here');
		//change the instructions at the top of the page
		$('.instructions p').text("Your device doesn't support saving. If you leave this page without sending your notes, they will be lost.");
		//keep the same notes function, but just don't do the autosave
		$('#notes').focus(function(e) {
			if ( $(this).val() == 'Write your notes here' ) {
				$(this).val('');
			}
		});
	}

	$('#notes').blur(function(e) {
		if ( $(this).val() == '' ) {
			$(this).val('Write your notes here');
		}
	});	

	$('#notes').autosize();

	$('#appNotes-email').hide();

	$('#appNotes-sendbtn').click(function(e) {

		e.preventDefault();

		//fade in email address field
		$(this).fadeOut({
			complete:function() {
				$('p.appNotes-disclaimer').hide();
				$('#appNotes-email').fadeIn();
				try {
					var savedEmail = localStorage.getItem('hcc-messageNotes-email');
					$('#email').val(savedEmail);
				} catch (e) {

				}			
			}
		});
	});

	$('#notesForm').submit(function(e) {

		e.preventDefault();

		//validate for email
		if ($('#email').val() == '') {
			//show error message
			$('input[name=email]').before('<p class="appNotes-emailError">Please enter your email</p>').fadeIn();
			return;
		} else {
			$('.appNotes-emailError').remove();
		}



		if (!$(this).hasClass('disabled') ) {

			$('#appNotes-notesSubmit').val('Sending...');
			$('#appNotes-notesSubmit').addClass('disabled');			

			var userNotes;

			if ($('#notes').val() == 'Write your notes here') {
				userNotesText = ''
			} else {
				userNotesText = $('#notes').val();
			}

			var formData = {
				staticNotes: $('#appNotes-staticNotes').html(),
				userNotes: userNotesText,
				userEmail:$('#email').val()
			}

			//send data to php file
			//controllerUrl is defined out on the main template
			$.ajax({
				type:'POST',
				url:controllerUrl,
				data:formData,
				dataType:'json',
				success:notesComplete,
				error:notesError
			});

			//save email in localStorage to be used later
			try {
				localStorage.setItem('hcc-messageNotes-email', $('#email').val());
			} catch(e) {

			}
		}

		//clear out notes
	});

	$(document).foundation();

});

function notesComplete(data,status,obj) {

	if (data[0].status == 'sent') {

		$('#appNotes-email').fadeOut({
			//reset the notes button
			complete:function() {
				window.scrollTo(0,0);
				$(this).find('#appNotes-notesSubmit').removeClass('disabled').val('Send');
				$('#notes').val('Write your notes here');
				$('#appNotes-success').fadeIn({
					duration:200,
					complete:function() {						
						setTimeout(function() {
							$('#appNotes-success').fadeOut();
							$('#appNotes-sendbtn').fadeIn();
							$('p.appNotes-disclaimer').show();
						},1200);					
					}
				});
			}
		});	
	} else {
		window.scrollTo(0,0);
		$('#appNotes-notesSubmit').removeClass('disabled').val('Send');
		$('#appNotes-error').fadeIn({
			duration:200,
			complete:function() {
				setTimeout(function() {
					$('#appNotes-error').fadeOut();
				},1200);					
			}
		});				
	}
}

function notesError(data,status,obj) {
	window.scrollTo(0,0);
	$('#appNotes-notesSubmit').removeClass('disabled').val('Send');
	$('#appNotes-error').fadeIn({
		duration:200,
		complete:function() {
			setTimeout(function() {
				$('#appNotes-error').fadeOut();
			},1200);					
		}
	});	
}

function autoSaveNotes() {
	//set a timer to run on keyup to save notes 1.5 seconds after you're done typing
	//if you start typing again it checks to see if an existing timeout is there and will only set one
	$('#notes').keyup(function() {
		if (window.hccNoteTimeout == null) {
			window.hccNoteTimeout = window.setTimeout(function() {
				try {
					localStorage.setItem('hcc-savedMessageNotes', $('#notes').val());	
				} catch(e) {
					return;
				}				
				console.log('notes saved');
				window.clearTimeout(window.hccNoteTimeout);
				window.hccNoteTimeout = null;
			}, 1500);
		}
	});
}